	getPagingValuesFromRecord: function(record){
		var values = {};		
		for (var name in record) {
			if (name.indexOf('.key') == -1 && name.indexOf('.raw') == -1 &&
				name != 'grid' && name != 'index' && name != 'row') {
					var value = new Object();
					values[name] = record[name];     
			}
		}
		return values;
	},

