		if(!valueExistsNotEmpty(this.assetTypes)) {
			var bl_id = (ar != null && ar.length > 0 ? ar[0] : null);
			var fl_id = (ar != null && ar.length > 1 ? ar[1] : null);
		}
		
		this.appendRecNonRm(bl_id, fl_id, id, fill, labels, asHighlight);
