INSERT INTO afm_flds(table_name,field_name,afm_type,allow_null,comments, data_type,decimals,dep_cols,dflt_val,edit_group,edit_mask, enum_list,is_atxt,max_val,min_val,ml_heading,afm_module, num_format,primary_key,ref_table,review_group,afm_size, sl_heading,string_format,is_tc_traceable) SELECT 'wrview','status_sort',2050,0,'status_sort',5,0,NULL,'0',NULL,NULL,NULL,0,NULL,NULL,'Status\x0D\x0ASort',4,0,0,NULL,NULL,1,NULL,25,0 FROM  DUAL WHERE NOT EXISTS (SELECT 1 FROM afm_flds WHERE table_name = 'wrview' AND field_name = 'status_sort');

CREATE OR REPLACE VIEW wrstat AS SELECT  TO_NUMBER( TO_CHAR( date_assigned, 'YYYY' )) || '-' ||  TO_NUMBER( TO_CHAR( date_assigned, 'MM' )) AS perform_date, prob_type, status, DECODE (status,  'R', 0, 'Rev', 1, 'Rej', 2, 'A', 3, 'AA', 4, 'I', 5, 'HP', 6, 'HA', 6, 'HL', 6, 'S', 7, 'Can', 8, 'Com', 9,  10 ) AS status_sort, COUNT(*) AS count_wr, SUM( est_labor_hours ) AS est_labor_sum, SUM( act_labor_hours ) AS act_labor_sum, (SUM(act_labor_hours) - SUM(est_labor_hours)) / COUNT(*) AS avg_labor_dif, SUM( cost_est_total ) AS est_cost_sum, SUM( cost_total ) AS act_cost_sum, (SUM(cost_total) - SUM(cost_est_total)) / COUNT(*) AS avg_cost_dif FROM wr GROUP BY  TO_NUMBER( TO_CHAR( date_assigned, 'YYYY' )) || '-' ||  TO_NUMBER( TO_CHAR( date_assigned, 'MM' )), prob_type, status;

CREATE OR REPLACE VIEW wrhwrstat AS(SELECT  TO_NUMBER( TO_CHAR( date_assigned, 'YYYY' )) || '-' ||  TO_NUMBER( TO_CHAR( date_assigned, 'MM' )) AS perform_date, prob_type, status, DECODE (status,  'R', 0, 'Rev', 1, 'Rej', 2, 'A', 3, 'AA', 4, 'I', 5, 'HP', 6, 'HA', 6, 'HL', 6, 'S', 7, 'Can', 8, 'Com', 9,  10 ) AS status_sort, COUNT(*) AS count_wr, SUM( est_labor_hours ) AS est_labor_sum, SUM( act_labor_hours ) AS act_labor_sum, (SUM(act_labor_hours) - SUM(est_labor_hours)) / COUNT(*) AS avg_labor_dif, SUM( cost_est_total ) AS est_cost_sum, SUM( cost_total ) AS act_cost_sum, (SUM(cost_total) - SUM(cost_est_total)) / COUNT(*) AS avg_cost_dif FROM wr GROUP BY  TO_NUMBER( TO_CHAR( date_assigned, 'YYYY' )) || '-' ||  TO_NUMBER( TO_CHAR( date_assigned, 'MM' )), prob_type, status) UNION ALL (SELECT  TO_NUMBER( TO_CHAR( date_assigned, 'YYYY' )) || '-' ||  TO_NUMBER( TO_CHAR( date_assigned, 'MM' )) AS perform_date, prob_type, status, DECODE (status,  'R', 0, 'Rev', 1, 'Rej', 2, 'A', 3, 'AA', 4, 'I', 5, 'HP', 6, 'HA', 6, 'HL', 6, 'S', 7, 'Can', 8, 'Com', 9,  10 ) AS status_sort, COUNT(*) AS count_wr, SUM( est_labor_hours ) AS est_labor_sum, SUM( act_labor_hours ) AS act_labor_sum, (SUM(act_labor_hours) - SUM(est_labor_hours)) / COUNT(*) AS avg_labor_dif, SUM( cost_est_total ) AS est_cost_sum, SUM( cost_total ) AS act_cost_sum, (SUM(cost_total) - SUM(cost_est_total)) / COUNT(*) AS avg_cost_dif FROM hwr GROUP BY  TO_NUMBER( TO_CHAR( date_assigned, 'YYYY' )) || '-' ||  TO_NUMBER( TO_CHAR( date_assigned, 'MM' )), prob_type, status);

CREATE OR REPLACE VIEW wostat AS SELECT  TO_NUMBER( TO_CHAR( date_assigned, 'YYYY' )) || '-' ||  TO_NUMBER( TO_CHAR( date_assigned, 'MM' )) AS perform_date, wo_type, tr_id, DECODE ( date_completed, NULL, 'Open', 'Complete') AS is_complete, COUNT(*) AS count_wo,  SUM( cost_estimated ) AS est_cost_sum,  SUM( cost_total ) AS act_cost_sum,  (SUM(cost_total) - SUM(cost_estimated)) / COUNT(*) AS avg_cost_dif FROM wo GROUP BY  TO_NUMBER( TO_CHAR( date_assigned, 'YYYY' )) || '-' ||  TO_NUMBER( TO_CHAR( date_assigned, 'MM' )), wo_type, tr_id, DECODE ( date_completed, NULL, 'Open', 'Complete');

CREATE OR REPLACE VIEW wohwostat AS(SELECT  TO_NUMBER( TO_CHAR( date_assigned, 'YYYY' )) || '-' ||  TO_NUMBER( TO_CHAR( date_assigned, 'MM' )) AS perform_date, wo_type, tr_id, DECODE ( date_completed, NULL, 'Open', 'Complete') AS is_complete, COUNT(*) AS count_wo,  SUM( cost_estimated ) AS est_cost_sum,  SUM( cost_total ) AS act_cost_sum,  (SUM(cost_total) - SUM(cost_estimated)) / COUNT(*) AS avg_cost_dif FROM wo GROUP BY  TO_NUMBER( TO_CHAR( date_assigned, 'YYYY' )) || '-' ||  TO_NUMBER( TO_CHAR( date_assigned, 'MM' )), wo_type, tr_id, DECODE ( date_completed, NULL, 'Open', 'Complete')) UNION ALL (SELECT  TO_NUMBER( TO_CHAR( date_assigned, 'YYYY' )) || '-' ||  TO_NUMBER( TO_CHAR( date_assigned, 'MM' )) AS perform_date, wo_type, tr_id, DECODE ( date_completed, NULL, 'Open', 'Complete') AS is_complete, COUNT(*) AS count_wo,  SUM( cost_estimated ) AS est_cost_sum,  SUM( cost_total ) AS act_cost_sum,  (SUM(cost_total) - SUM(cost_estimated)) / COUNT(*) AS avg_cost_dif FROM hwo GROUP BY  TO_NUMBER( TO_CHAR( date_assigned, 'YYYY' )) || '-' ||  TO_NUMBER( TO_CHAR( date_assigned, 'MM' )), wo_type, tr_id, DECODE ( date_completed, NULL, 'Open', 'Complete'));

ALTER TABLE wr MODIFY time_requested DATE DEFAULT SYSDATE;